IOY0     EQU  0600H        		;IOY0起始地址A8254    EQU  IOY0+00H*2B8254    EQU  IOY0+01H*2C8254    EQU  IOY0+02H*2CON8254  EQU  IOY0+03H*2SSTACK	SEGMENT STACK		DW 32 DUP(?)		SSTACK	ENDS        CODE	SEGMENT        		ASSUME CS:CODE, SS:SSTACKSTART:	PUSH DS		MOV BX,0134H		MOV AX, 0000H		MOV DS, AX		MOV AX, OFFSET IRQ7			;取中断入口地址		MOV SI, 003CH				;中断矢量地址		MOV [SI], AX				;填IRQ7的偏移矢量		MOV AX, CS				;段地址		MOV SI, 003EH		MOV [SI], AX				;填IRQ7的段地址矢量		CLI		POP DS		;初始化主片8259		MOV AL, 11H		OUT 20H, AL				;ICW1		MOV AL, 08H		OUT 21H, AL				;ICW2		MOV AL, 04H		OUT 21H, AL				;ICW3		MOV AL, 01H		OUT 21H, AL				;ICW4		MOV AL, 6FH				;OCW1		OUT 21H, AL		;8254		MOV DX, CON8254		MOV AL, 16H				;计数器0，方式0		OUT DX, AL		MOV DX, A8254		MOV AL, 04H		OUT DX, AL		STIAA1:	JMP AA1IRQ7:	MOV DX, A8254        		MOV AL, 04H		OUT DX, AL		MOV AX, 0143H		INT 10H		MOV AX ,014FH		INT 10H		MOV AX ,0155H		INT 10H		MOV AX ,014EH		INT 10H		MOV AX ,0154H		INT 10H		MOV AX,BX		INT 10H		DEC BX		CMP BX, 012FH		JE	ADDBXCONT:	MOV AX, 0120H		INT 10H				MOV AL, 20H		OUT 20H, AL				;中断结束命令		IRET		ADDBX:  ADD BX, 05H		JMP CONTCODE	ENDS		END  START